        uint256 totalValue = 0;
        for (uint256 i = 0; i < s_allowedTokens.length; i++) {
            (uint256 price, uint256 decimals) = getLatestPrice(s_allowedTokens[i]);

            totalValue += ((price / 10**decimals) * s_totalSupply[s_allowedTokens[i]]);
        }
        return totalValue;
    }

